!(function (s) {
    "use strict";

    s("body").on("click", ".xs-categories-toggle", function (e) {
        e.target === this &&
            (s(this).hasClass("show-items")
                ? s(this).removeClass("show-items")
                : s(this).addClass("show-items"));
    });

    s("body").on("click", function (e) {
        s(e.target).closest(".menu-category").length ||
            s(".xs-categories-toggle").removeClass("show-items");
    });

    s("body").on("click", ".cat-dropdown-menu > li", function () {
        var o = s(this),
            t = o.hasClass("show-sub-menu");

        s(".cat-dropdown-menu > li").removeClass("show-sub-menu");
        t || o.addClass("show-sub-menu");
    });

    s("body").on("click", "#navbarClose", function () {
        s("#navbarContent").removeClass("show");
    });

    s("body").on("click", "#navbarToggle", function () {
        s("#navbarContent").addClass("show");
    });

    /* =============================
       NAVBAR STICKY (SAFE CHECK)
    ============================== */
    var navbar = document.getElementById("navbar");
    var navbarVacuum = document.getElementById("navbarVacuum");

    if (navbar && navbarVacuum) {
        var offsetTop = navbar.offsetTop;

        window.addEventListener("scroll", function () {
            if (window.pageYOffset >= offsetTop) {
                navbarVacuum.style.height = navbar.offsetHeight + "px";
                navbar.classList.add("sticky");
            } else {
                navbarVacuum.style.height = "0";
                navbar.classList.remove("sticky");
            }
        });
    }

})(jQuery);
